 . __pick_a_word () if last_guess == guess : # here fix a bug in the original solver self . __candidate_words . remove ( guess ) guess = self . __pick_a_word () result , letter_verdicts = wordle . guess ( guess ) last_guess = guess self . tries . append ( guess ) if result == AttemptVerdict . WON : return hex_id , guess elif result == AttemptVerdict . LOST : return False elif result == AttemptVerdict . FAILED_ATTEMPT : for chr in guess : self . __untried_letters . discard ( chr ) for i in range ( len ( letter_verdicts )): letter , verdict = letter_verdicts [ i ] if verdict == LetterVerdict . GRAY : flag = True for ( l , _ ) in self . __yellow_blocks : # here fix a bug in if l == letter : # the original solver flag = False break if flag : self . __invalid_letters . add ( letter ) elif verdict == LetterVerdict . GREEN : self . __green_blocks . add (( letter , i )) if ( letter , i ) in self . __yellow_blocks : self . __yellow_blocks . remove (( letter , i )) elif verdict == LetterVerdict . YELLOW : self . __yellow_blocks . add (( letter , i )) else : exit ( 1 ) elif result == AttemptVerdict . INVALID_WORD : self . attempt -= 1 self . __candidate_words . remove ( guess ) self . __all_possible_words . remove ( guess ) class Wordle : def __init__ ( self , mode ): self . p = connect ( "47.106.102.129" , 23370 ) self . p . sendlineafter ( b "> " , str ( mode ) . encode ( "utf-8" )) def restart ( self , mode ): self . p . sendlineafter ( b "> " , str ( mode ) . encode ( "utf-8" )) def guess ( self , word ): self . p . sendlineafter ( b "> " , word . encode ( "utf-8" )) status = self . p . recvuntil ( b "!" ) if status == b "Correct!" : return AttemptVerdict . WON , None result = self . p . recvline () . decode ( "utf-8" ) res = re . findall ( r "\[4(\d)m" , result ) ret = [] for i in range ( 5 ): c = word [ i ] if res [ i ] == "7" : ret . append (( c , LetterVerdict . GRAY )) elif res [ i ] == "3" : ret . append (( c , LetterVerdict . YELLOW )) else : ret . append (( c , LetterVerdict . GREEN )) attempt_verdict = AttemptVerdict . WON for _ , verdict in ret : if verdict != LetterVerdict . GREEN : attempt_verdict = AttemptVerdict . FAILED_ATTEMPT break return attempt_verdict , ret cracker = RandCrack () wordle = Wordle ( 0 ) solver = WordleSolver () def calc_random_id ( hex_id , word ): valid_words = get_all_wordle_words () length = len ( valid_words ) index = valid_words . index ( word ) # id % len(valid_words) Id = eval ( "0x" + hex_id ) tmp = Id ^ index # id // len(valid_words) return tmp * length + index def calc_hex_id_and_word ( random_value ): valid_words = get_all_wordle_words () length = len ( valid_words ) word = valid_words [ random_value % length ] hex_value = ( random_value // length ) ^ ( random_value % length ) return hex ( hex_value )[ 2 :] . zfill ( 5 ), word with tqdm ( total = 624 , desc = "Cracking random..." ) as pbar : for i in range ( 512 ): solver . reset () hex_id , answer = solver . solve ( wordle ) random_value = calc_random_id ( hex_id , answer ) cracker . submit ( random_value ) pbar . update ( 1 ) wordle . restart ( 0 ) for i in range ( 112 ): solver . reset () hex_id , answer = solver . solve ( wordle ) random_value = calc_random_id ( hex_id , answer ) cracker . submit ( random_value ) pbar . update ( 1 ) with tqdm ( total = 400 , desc = "Verifying crack result..." ) as pbar : for i in range ( 400 ): solver . reset () predict = cracker . predict_randrange ( 4090 * ( 2 ** 20 )) p_hex_id , p_answer = calc_hex_id_and_word ( predict ) hex_id , answer = solver . solve ( wordle ) if p_hex_id != hex_id : print ( f "Crack faild: 
