    constructor(address addr) public {
        challenge = GatekeeperTwo(addr);
        uint64 key = uint64(bytes8(keccak256(abi.encodePacked(this)))) ^ uint64(0xFFFFFFFFFFFFFFFF);
        challenge.enter(bytes8(key));
    }
}
</code></pre>

