pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/proxy/UpgradeableProxy.sol";

contract PuzzleProxy is UpgradeableProxy {
    address public pendingAdmin;
    address public admin;

    constructor(address _admin, address _implementation, bytes memory _initData) UpgradeableProxy(_implementation, _initData) public {
        admin = _admin;
    }

    modifier onlyAdmin {
        require(msg.sender == admin, "Caller is not the admin");
        _;
    }

    function proposeNewAdmin(address _newAdmin) external {
        pendingAdmin = _newAdmin;
    }

    function approveNewAdmin(address _expectedAdmin) external onlyAdmin {
        require(pendingAdmin == _expectedAdmin, "Expected new admin by the current admin is not the pending admin");
        admin = pendingAdmin;
    }

    function upgradeTo(address _newImplementation) external onlyAdmin {
        _upgradeTo(_newImplementation);
    }
}

contract PuzzleWallet {
    using SafeMath for uint256;
    address public owner;
    uint256 public maxBalance;
    mapping(address =&gt; bool) public whitelisted;
    mapping(address =&gt; uint256) public balances;

    function init(uint256 _maxBalance) public {
        require(maxBalance == 0, "Already initialized");
        maxBalance = _maxBalance;
        owner = msg.sender;
    }

    modifier onlyWhitelisted {
        require(whitelisted[msg.sender], "Not whitelisted");
        _;
    }

    function setMaxBalance(uint256 _maxBalance) external onlyWhitelisted {
        require(address(this).balance == 0, "Contract balance is not 0");
        maxBalance = _maxBalance;
    }

    function addToWhitelist(address addr) external {
        require(msg.sender == owner, "Not the owner");
        whitelisted[addr] = true;
    }

    function deposit() external payable onlyWhitelisted {
        require(address(this).balance &lt;= maxBalance, "Max balance reached");
        balances[msg.sender] = balances[msg.sender].add(msg.value);
    }

    function execute(address to, uint256 value, bytes calldata data) external payable onlyWhitelisted {
        require(balances[msg.sender] &gt;= value, "Insufficient balance");
        balances[msg.sender] = balances[msg.sender].sub(value);
        (bool success, ) = to.call{ value: value }(data);
        require(success, "Execution failed");
    }

    function multicall(bytes[] calldata data) external payable onlyWhitelisted {
        bool depositCalled = false;
        for (uint256 i = 0; i &lt; data.length; i++) {
            bytes memory _data = data[i];
            bytes4 selector;
            assembly {
                selector := mload(add(_data, 32))
            }
            if (selector == this.deposit.selector) {
                require(!depositCalled, "Deposit can only be called once");
                // Protect against reusing msg.value
                depositCalled = true;
            }
            (bool success, ) = address(this).delegatecall(data[i]);
            require(success, "Error while delegating call");
        }
    }
}
</code></pre> <p>题目比较复杂，最终目的是使 PuzzleProxy 的 admin 为 player</p> <p>因为 PuzzleProxy 和 PuzzleWallet 部署在同一个地址上，它们的 storage 会共用，所以就导致了 PuzzleProxy 的 pendingAdmin 对应了 PuzzleWallet 的 owner，PuzzleWallet 的 maxBalance 对应了 PuzzleProxy 的 admin</p> <p>从后往前推：</p> <ul> <li>要使 admin 变为 player，则可以设置 maxBalance 为 player</li> <li>而 setMaxBalance 函数需要先使当前合约账户的余额变为 0</li> <li>合约账户余额减少的方式在 execute 中的 call，使余额减少 value</li> <li>但这样要使 balances[player] &gt;= value</li> <li>而通过 deposit 增加 balances[player] 的话，合约账户余额也会同步增加</li> <li>所以要使用 multicall 函数来使 balances[player] 增加量为合约账户余额增加量的二倍</li> <li>但是 multicall 中检测了 selector 使 deposit 只能调用一次</li> <li>这可以通过 multicall 中执行两个 multicall，每个 multicall 调用一次 deposit</li> </ul> <p>这样分析之后问题就解决了，首先调用 proposeNewAdmin 来使 pendingAdmin（owner）变为 player，这个函数不能直接调用，但是可以通过发送合约的方式调用 <pre><code>&gt; await web3.eth.getStorageAt(instance, 0)  // owner
